export const dropdownFn=()=>{[...document.querySelectorAll(".-hm-dropdown")].forEach((e=>{const o=e.querySelector(".-hm-dropdown__opener"),r=e.querySelector(".-hm-dropdown__value"),t=e.querySelectorAll(".-hm-dropdown__item");o.addEventListener("click",(()=>{o.classList.toggle("active")})),t.forEach((e=>{e.addEventListener("click",(()=>{o.classList.remove("active"),r.innerText=e.innerText}))}))}))};